import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import logo from '../assets/images/logo-01-01.png';
import '../App.css';

export default function Navigation() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const isLoggedIn = sessionStorage.getItem('token') !== null;

  const handleLogout = () => {
    // Xử lý logout tại đây
    // Xóa token và các thông tin khác khỏi sessionStorage
    sessionStorage.removeItem('token');
    sessionStorage.removeItem('expirationTime');
    // Chuyển hướng đến trang đăng nhập
    window.location.href = '/login';
  };

  const handleOptionClick = () => {
    setIsModalOpen(false); // Đặt giá trị isModalOpen thành false khi người dùng nhấp vào tùy chọn
  };

  return (
    <div className="nav">
      <nav>
        <ul className="nav-list">
          {/* Các phần tử nav-list */}
        </ul>
        {isLoggedIn ? (
          <div className="nav-list--prior">
            <div className="avatar" onClick={() => setIsModalOpen(true)}>
              {/* Hiển thị avatar ở đây */}
            </div>
            <Modal
              isOpen={isModalOpen}
              onRequestClose={() => setIsModalOpen(false)}
              contentLabel="Options Modal"
              className="options-modal"
            >
              <ul className="options-list">
                <li>
                  <Link to="/customer/profile" onClick={handleOptionClick}>
                    Xem thông tin cá nhân
                  </Link>
                </li>
                <li>
                  <Link to="/customer/profile/edit" onClick={handleOptionClick}>
                    Chỉnh sửa thông tin
                  </Link>
                </li>
                <li>
                  <Link to="/customer/treatmentprofile/treatment" onClick={handleOptionClick}>
                    Xem hồ sơ bệnh
                  </Link>
                </li>
                <li>
                  <Link to="/admin/changepass" onClick={handleOptionClick}>
                    Đổi mật khẩu
                  </Link>
                </li>
                <li>
                  <Link to="/logout" onClick={handleLogout}>
                    Đăng xuất
                  </Link>
                </li>
              </ul>
            </Modal>
          </div>
        ) : (
          <div className="nav-list--prior">
            <Link to={'/Login'}>Login</Link>
          </div>
        )}
      </nav>
    </div>
  );
}
