import React from 'react';

function Pagination({ currentPage, totalPages, onPageChange }) {
  const renderPageNumbers = () => {
    const pageNumbers = [];

    // Calculate the previous and next page numbers
    const prevPage = currentPage > 1 ? currentPage - 1 : null;
    const nextPage = currentPage < totalPages ? currentPage + 1 : null;

    // Render the start index if not already on the first page
    if (currentPage !== 1) {
      pageNumbers.push(
        <li key={1}>
          <a href="#" onClick={() => onPageChange(1)}>
            1
          </a>
        </li>
      );
    }

    // Render the previous page number if available
    if (prevPage !== null) {
      pageNumbers.push(
        <li key={prevPage}>
          <a href="#" onClick={() => onPageChange(prevPage)}>
            {prevPage}
          </a>
        </li>
      );
    }

    // Render the current page number
    pageNumbers.push(
      <li key={currentPage} className="active">
        <a href="#">{currentPage}</a>
      </li>
    );

    // Render the next page number if available
    if (nextPage !== null) {
      pageNumbers.push(
        <li key={nextPage}>
          <a href="#" onClick={() => onPageChange(nextPage)}>
            {nextPage}
          </a>
        </li>
      );
    }

    // Render the end index if not already on the last page
    if (currentPage !== totalPages) {
      pageNumbers.push(
        <li key={totalPages}>
          <a href="#" onClick={() => onPageChange(totalPages)}>
            {totalPages}
          </a>
        </li>
      );
    }

    return pageNumbers;
  };

  return <ul className="pagination">{renderPageNumbers()}</ul>;
}

export default Pagination;
