import React from 'react';

function Pagination({ currentPage, totalPages, onPageChange }) {
  const renderPageNumbers = () => {
    const pageNumbers = [];

    // Render the previous page number if available
    if (currentPage > 1) {
      pageNumbers.push(
        <li key="prev">
          <a href="#" onClick={() => onPageChange(currentPage - 1)}>
            Previous
          </a>
        </li>
      );
    }

    // Render the current page number
    pageNumbers.push(
      <li key={currentPage} className="active">
        <a href="#">{currentPage}</a>
      </li>
    );

    // Render the next page number if available
    if (currentPage < totalPages) {
      pageNumbers.push(
        <li key="next">
          <a href="#" onClick={() => onPageChange(currentPage + 1)}>
            Next
          </a>
        </li>
      );
    }

    return pageNumbers;
  };

  return <ul className="pagination">{renderPageNumbers()}</ul>;
}

export default Pagination;
