import React, { useState } from 'react';
import './viewBooking.css';

const ViewBooking = () => {
    const [currentPage, setCurrentPage] = useState(1);
    const bookingsPerPage = 5;

    const bookings = [
        {
            id: 1,
            patientName: 'John Doe',
            doctorName: 'Dr. Emily Nguyen',
            date: '2023-07-01',
            time: '10:00 AM',
            status: 'Confirmed',
        },
        {
            id: 2,
            patientName: 'Jane Smith',
            doctorName: 'Dr. David Johnson',
            date: '2023-07-02',
            time: '11:00 AM',
            status: 'Confirmed',
        },
        // ...
        // Rest of the bookings
        // ...
    ];

    const pagesCount = Math.ceil(bookings.length / bookingsPerPage);

    const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

    const renderBookings = () => {
        const startIndex = (currentPage - 1) * bookingsPerPage;
        const endIndex = startIndex + bookingsPerPage;
        const currentBookings = bookings.slice(startIndex, endIndex);

        return currentBookings.map((booking) => (
            <div className="booking" key={booking.id}>
                <div className="booking-info">
                    <p className="booking-patient">{booking.patientName}</p>
                    <p className="booking-doctor">{booking.doctorName}</p>
                    <p className="booking-date">{booking.date}</p>
                    <p className="booking-time">{booking.time}</p>
                </div>
                <div className="booking-status">
                    <p className={`booking-status-${booking.status.toLowerCase()}`}>
                        {booking.status}
                    </p>
                </div>
            </div>
        ));
    };

    const renderPaginationItems = () => {
        const paginationItems = [];

        for (let i = 1; i <= pagesCount; i++) {
            paginationItems.push(
                <button
                    key={i}
                    className={`pagination-button ${i === currentPage ? 'active' : ''}`}
                    onClick={() => handlePageChange(i)}
                >
                    {i}
                </button>
            );
        }

        return paginationItems;
    };

    return (
        <div className="view-booking">
            <h1>Danh s√°ch Booking</h1>
            <div className="booking-list">
                {renderBookings()}
            </div>
            <div className="pagination">
                {currentPage > 1 && (
                    <button
                        className="pagination-button"
                        onClick={() => handlePageChange(currentPage - 1)}
                    >
                        &lt;
                    </button>
                )}
                {renderPaginationItems()}
                {currentPage < pagesCount && (
                    <button
                        className="pagination-button"
                        onClick={() => handlePageChange(currentPage + 1)}
                    >
                        &gt;
                    </button>
                )}
            </div>
        </div>
    );
};

export default ViewBooking;
