import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminCreateSlot.css';

function CreateSchedulePage() {
  const [searchValue, setSearchValue] = useState('');
  const [doctors, setDoctors] = useState([]);
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    // Gửi yêu cầu GET tới API để lấy danh sách bác sĩ
    axios
      .get('http://localhost:3000/api/doctors')
      .then((response) => {
        setDoctors(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const handleSearchDoctor = () => {
    if (searchValue === '') {
      setSearchResults([]);
    } else {
      const results = doctors.filter((doctor) =>
        doctor.fullname.toLowerCase().includes(searchValue.toLowerCase())
      );
      setSearchResults(results);
    }
  };

  return (
    <div className="page-container">
      <div className="admin-create-slot__container">
        <h2>Tạo Lịch Làm Việc</h2>

        {/* Tìm kiếm bác sĩ */}
        <div>
          <h3>Tìm kiếm bác sĩ</h3>
          <input
            type="text"
            placeholder="Nhập tên bác sĩ..."
            value={searchValue}
            onChange={(e) => setSearchValue(e.target.value)}
          />
          <button onClick={handleSearchDoctor}>Tìm kiếm</button>
        </div>

        {/* Hiển thị danh sách bác sĩ phù hợp */}
        <div>
          <h3>Kết quả tìm kiếm</h3>
          {searchResults.length > 0 ? (
            <ul>
              {searchResults.map((doctor) => (
                <li key={doctor.id}>
                  <img src={doctor.avatar} alt={doctor.fullname} />
                  {doctor.fullname.substring(0, searchValue.length)}
                </li>
              ))}
            </ul>
          ) : (
            <p>Không tìm thấy bác sĩ phù hợp</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default CreateSchedulePage;
